mapper.insert:
	INSERT INTO REPLY(
                        REPLY_NUM
                        , CONTENT
                        , CREATE_DATE
                        , MEMBER_ID
                        , BOARD_NUM)
            VALUES(
            (SELECT NVL(MAX(REPLY_NUM) + 1, 1) FROM REPLY)
			, #{content}
			, SYSDATE
			, #{memberId}
			, #{boardNum}
			)
